(ns elevator-test
  (:require [clojure.test :refer :all]))

(load-file "elevator.clj")

(deftest ground-floor-nested
  (is (= 0 (elevator/arrival-floor "(())"))))
  
(deftest ground-floor-unnester
  (is (= 0 (elevator/arrival-floor "()()"))))

(deftest third-floor-open-only
  (is (= 3 (elevator/arrival-floor "((("))))
 
(deftest third-floor-open-a
  (is (= 3 (elevator/arrival-floor "(()(()("))))
  
(deftest third-floor-open-b
  (is (= 3 (elevator/arrival-floor "))((((("))))

(deftest basement-a
  (is (= -1 (elevator/arrival-floor "())"))))
  
(deftest basement-b
  (is (= -1 (elevator/arrival-floor "))("))))
  
(deftest third-sub-floor-a
  (is (= -3 (elevator/arrival-floor ")))"))))

(deftest third-sub-floor-b
  (is (= -3 (elevator/arrival-floor ")())())"))))

