class Elevator
  UP   = '('
  DOWN = ')'
  
  def self.basement dirs = ''
    floor = 0

    dirs.each_char.each_with_index do |c, i|
      floor += 1   if c == UP
      floor -= 1   if c == DOWN
      return (i+1) if floor == -1
    end

    nil
  end
end

describe Elevator do
  context 'part two of of challenge one from http://adventofcode.com/' do
    describe '.basement' do
      it 'returns nil when given no directions' do
        expect(Elevator.basement).to be_nil
      end
      
      it 'returns nil if we never each the basement' do
        expect(Elevator.basement('(((')).to be_nil
      end
      
      it 'returns 1 when we immediately travel to the basement' do
        expect(Elevator.basement(')')).to equal(1)
      end
      
      it 'returns 5 after two round trips to ground followed by the basement' do
        expect(Elevator.basement('()())')).to equal(5)
      end
      
